import React, { Component } from "react";
import { Button, Segment, Table, Label, Input} from "semantic-ui-react";


const API = `http://localhost:8080/api/school`;

class School extends Component {
  constructor(props) {
    super(props);
    this.handleClickEdit = this.handleClickEdit.bind(this);
    this.handleClickAdd = this.handleClickAdd.bind(this);

    this.state = {
      isLoading: true,
      selected: false,
      update: false,
      addSchool: false,
      schools: [],
      selectedSchool: []
    };
  }

  //FETCH SCHOOLS
  async componentDidMount() {
    const response = await fetch(API, {
      method: "GET",
      credentials: "include",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      }
    });
    console.log(response);
    const body = await response.json();
    this.setState({ schools: body, isLoading: false, update: false });
  }



  //REMOVE SCHOOL
  async remove(schoolId) {
    console.log(schoolId + ` is deleted. `);
    await fetch(`http://localhost:8080/api/school/${schoolId}`, {
      method: "DELETE",
      headers: {
        'Accept': "application/json",
        "Content-Type": "application/json"
      }
    }).then(() => {
      window.location.reload();
    });
  }



  render() {
    const { schools } = this.state;

    return (
      <div>
        <Segment inverted>
          <Table.Cell>
          <Table.Row> 
                      <Table.Cell>
                              <Input ref="schoolName" placeholder="School Name" name="sn" onChange={e => this.handleChange(e)}></Input>
                      </Table.Cell>
                      <Table.Cell>
                             <Input ref="schoolAddress" placeholder="School Address" name="sa" onChange={e => this.handleChange(e)}></Input>
                      </Table.Cell>
                      <Table.Cell>
                            <Input ref="schoolPhone" placeholder="School Phone" name="sp" onChange={e => this.handleChange(e)}></Input>
                      </Table.Cell>
                            <Button inverted type="submit" color="yellow"  onClick={(e) =>  this.handleClickAdd(e)} > Add </Button>
                            <Label as="a" basic color="blue"> SCHOOLS </Label>
                </Table.Row>
          </Table.Cell>
        </Segment>
        <Table ui selectable inverted table>
          <Table.Header>
            <Table.Row>
              <Table.HeaderCell>Name</Table.HeaderCell>
              <Table.HeaderCell>Address</Table.HeaderCell>
              <Table.HeaderCell>Phone</Table.HeaderCell>
              <Table.HeaderCell>Settings</Table.HeaderCell>
            </Table.Row>
          </Table.Header>
          {schools.map(school => (
            <Table.Row id={school.schoolId}>
              <Table.Cell>{school.schoolName}</Table.Cell>
              <Table.Cell>{school.schoolAddress}</Table.Cell>
              <Table.Cell>{school.schoolPhone}</Table.Cell>
              <Segment inverted>
                 <Table.Cell>
                    <Button type="button" inverted color="teal" onClick={() => {this.handleClickEdit(school.schoolId); }} > Edit </Button>
                 </Table.Cell>
                <Table.Cell>
                  <Button type="button" inverted  color="red" onClick={() => { this.remove(school.schoolId); }} > Delete </Button>                    
                </Table.Cell>
              </Segment>
            </Table.Row>
          ))}
        </Table>
      </div>
    );
  }
}

export default School;
