import React, { Component } from "react";
import { Button, Segment, Table, Label } from "semantic-ui-react";

const API = `http://localhost:8080/api/student`;

class Student extends Component {
  constructor(props) {
    super(props);
    this.handleChangeIN = this.handleChangeIN.bind(this);
    this.handleChangeIS = this.handleChangeIS.bind(this);
    this.handleChangeID = this.handleChangeID.bind(this);
    this.handleChangeIC = this.handleChangeIC.bind(this);
    this.handleChangeIP = this.handleChangeIP.bind(this);
    this.handleChangeISchool = this.handleChangeISchool.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
    this.onEdit = this.onEdit.bind(this);


    this.state = {
      isLoading: false,
      students: [],
      schools : [],
      selectedStudent: [],
      addStudent: false
    };

    this.studentEdit = {
      studentName : '',
      studentSurname: '',
      studentCity: '',
      studentDistinct: '',
      studentPhone: '',
      studentSchool: ''
    };
  }

  async componentDidMount() {
    const response = await fetch(API, {
      method: "GET",
      credentials: "include",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      }
    });
    const body = await response.json();
    this.setState({ students: body, isLoading: false });
  }

  //EDIT SCHOOL
  async handleClickEdit(studentId) {
    await fetch(`http://localhost:8080/api/school/${studentId}`, {
      method: "PUT",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      }
    }).then(async response => {
      this.setState({
        addSchool: false,
        selectedStudent: await response.json()
      });
    });
  }

  //REMOVE STUDENT
  async remove(studentId) {
    await fetch(`http://localhost:8080/api/student/${studentId}`, {
      method: "DELETE",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      }
    }).then(() => {
      window.location.reload();
    });
  }

  render() {
    const { students } = this.state;
    return (
      <div>
        <form id='inputlar' onSubmit={this.onSubmit}>
          <FormGroup>
           <Segment inverted>
            <Table.Cell>
                <Table.Row> 
                        <Table.Cell>
                                <input type="text" id="sname" defaultValue={this.studentEdit.studentName} placeholder="Name" name="sn" onChange={this.handleChangeIN}/>
                        </Table.Cell>
                        <Table.Cell>
                                <input type="text" id="ssurname" defaultValue={this.studentEdit.studentSurname} placeholder="Surname" name="is" onChange={this.handleChangeIS}/>
                        </Table.Cell>
                        <Table.Cell>
                                <input type="text" id="sphone" defaultValue={this.studentEdit.studentPhone} placeholder="Branch" name="ib" onChange={this.handleChangeIB}/>
                        </Table.Cell>
                        <Table.Cell>
                                <input type="text" id="scity" defaultValue={this.studentEdit.studentCity} placeholder="Age" name="ia" onChange={this.handleChangeIC}/>
                        </Table.Cell>
                        <Table.Cell>
                              <input type="text" id="sdistinct" defaultValue={this.studentEdit.studentDistinct} placeholder="Phone" name="ip" onChange={this.handleChangeID}></input>
                        </Table.Cell>
                        <Table.Cell>
<select onChange={this.handleChangeISchool} id='sschool' name='isc'>{this.state.schools.map(school => 
  <option id={school.schoolName} value={school.schoolName}>{school.schoolName}</option> )}
  </select>              
         </Table.Cell>
                              <Button inverted type="submit" color="yellow" onSubmit={this.onSubmit} > Save </Button>
                              <Label as="a" basic color="blue"> SCHOOLS </Label>
                  </Table.Row>
            </Table.Cell>
          </Segment>
        </FormGroup>
      </form>
        <Table inverted>
          <Table.Header>
            <Table.Row>
              <Table.HeaderCell>Name</Table.HeaderCell>
              <Table.HeaderCell>Surname</Table.HeaderCell>
              <Table.HeaderCell>Phone</Table.HeaderCell>
              <Table.HeaderCell>City</Table.HeaderCell>
              <Table.HeaderCell>District</Table.HeaderCell>
              <Table.HeaderCell>School</Table.HeaderCell>
              <Table.HeaderCell>Settings</Table.HeaderCell>
            </Table.Row>
          </Table.Header>
          {students.map(student => (
            <Table.Row id={student.studentId}>
              <Table.Cell>{student.name}</Table.Cell>
              <Table.Cell>{student.surname}</Table.Cell>
              <Table.Cell>{student.mobilePhone}</Table.Cell>
              <Table.Cell>{student.city}</Table.Cell>
              <Table.Cell>{student.district}</Table.Cell>
              <Table.Cell>{student.studentSchool}</Table.Cell>
              <Segment inverted>
                <Table.Cell>
                  <Button
                    type="button"
                    inverted
                    color="teal"
                    onClick={() => {
                      this.handleClickEdit(student.studentId);
                    }}
                  >
                    {" "}
                    Edit{" "}
                  </Button>
                </Table.Cell>
                <Table.Cell>
                  <Button type="button" inverted color="red"
                    onClick={() => {  this.remove(student.studentId); }}  >
                     {" "}Delete{" "}
                  </Button>
                </Table.Cell>
              </Segment>
            </Table.Row>
          ))}
        </Table>
      </div>
    );
  }
}

export default Student;
