import React, { Component } from "react";
import { Button, Segment, Table, Label } from "semantic-ui-react";

const API = `http://localhost:8080/api/student`;

class Student extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoading: false,
      students: [],
      selectedStudent: [],
      addStudent: false
    };
  }

  async componentDidMount() {
    const response = await fetch(API, {
      method: "GET",
      credentials: "include",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      }
    });
    const body = await response.json();
    this.setState({ students: body, isLoading: false });
  }

  //EDIT SCHOOL
  async handleClickEdit(studentId) {
    await fetch(`http://localhost:8080/api/school/${studentId}`, {
      method: "PUT",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      }
    }).then(async response => {
      this.setState({
        addSchool: false,
        selectedStudent: await response.json()
      });
    });
  }

  //REMOVE STUDENT
  async remove(studentId) {
    await fetch(`http://localhost:8080/api/student/${studentId}`, {
      method: "DELETE",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      }
    }).then(() => {
      window.location.reload();
    });
  }

  render() {
    const { students } = this.state;
    return (
      <div>
        <Segment inverted>
          <Table.Cell>
            <Button type="button" inverted color="yellow" onClick={() => {
              this.handleClickAdd(students.studentId); }}>
              {" "}
              Add{" "}
            </Button>
            <Label as="a" basic color="blue">
              STUDENT
            </Label>
          </Table.Cell>
        </Segment>
        <Table inverted>
          <Table.Header>
            <Table.Row>
              <Table.HeaderCell>Name</Table.HeaderCell>
              <Table.HeaderCell>Surname</Table.HeaderCell>
              <Table.HeaderCell>Phone</Table.HeaderCell>
              <Table.HeaderCell>City</Table.HeaderCell>
              <Table.HeaderCell>District</Table.HeaderCell>
              <Table.HeaderCell>School</Table.HeaderCell>
              <Table.HeaderCell>Settings</Table.HeaderCell>
            </Table.Row>
          </Table.Header>
          {students.map(student => (
            <Table.Row id={student.studentId}>
              <Table.Cell>{student.name}</Table.Cell>
              <Table.Cell>{student.surname}</Table.Cell>
              <Table.Cell>{student.mobilePhone}</Table.Cell>
              <Table.Cell>{student.city}</Table.Cell>
              <Table.Cell>{student.district}</Table.Cell>
              <Table.Cell>{student.studentSchool}</Table.Cell>
              <Segment inverted>
                <Table.Cell>
                  <Button
                    type="button"
                    inverted
                    color="teal"
                    onClick={() => {
                      this.handleClickEdit(student.studentId);
                    }}
                  >
                    {" "}
                    Edit{" "}
                  </Button>
                </Table.Cell>
                <Table.Cell>
                  <Button type="button" inverted color="red"
                    onClick={() => {  this.remove(student.studentId); }}  >
                     {" "}Delete{" "}
                  </Button>
                </Table.Cell>
              </Segment>
            </Table.Row>
          ))}
        </Table>
      </div>
    );
  }
}

export default Student;
