import React, { Component } from "react";
import { Button, Segment, Table, Label, Input, FormGroup} from "semantic-ui-react";


const API = `http://localhost:8080/api/school`;

class School extends Component {
  constructor(props) {
    super(props);

    this.handleClickEdit = this.handleClickEdit.bind(this);
    this.handleChangeSN = this.handleChangeSN.bind(this);
    this.handleChangeSA = this.handleChangeSA.bind(this);
    this.handleChangeSP = this.handleChangeSP.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      isLoading: true,
      selected: false,
      addSchool: false,
      update: false,
      schools: [],
      selectedSchool: [],
      // to add school
      sid : '',
      schoolName : '' , //school Name
      schoolAddress : '' , //school Address
      schoolPhone : ''   //school Phone
    };

  }

  //FETCH SCHOOLS
  async componentDidMount() {
    const response = await fetch(API, {
      method: "GET",
      credentials: "include",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      }
    });
    console.log(response);

    const body = await response.json();
    this.setState({ schools: body, isLoading: false, update: false });
  }


  //EDIT SCHOOL
  async handleClickEdit(schoolId) {
    this.setState= { 
      isLoading : false,
      update: true
    }
  }


  //REMOVE SCHOOL
  async remove(schoolId) {
    console.log(schoolId + ` is deleted. `);
    await fetch(`http://localhost:8080/api/school/${schoolId}`, {
      method: "DELETE",
      headers: {
        'Accept': "application/json",
        "Content-Type": "application/json"
      }
    }).then(() => {
      window.location.reload();
    });
  }

  //  ADD SCHOOL 

  handleChangeSN = (e) => {
    this.setState({
        schoolName: e.target.value
    })
  }
  handleChangeSA = (e) => {
    this.setState({
        schoolAddress: e.target.value
    })
  }
  handleChangeSP = (e) => {
    this.setState({
        schoolPhone: e.target.value
    })
  }

  async onSubmit(e) {
    e.preventDefault();
    let body = "";
    let response = "";
    try {
      response = await fetch(API, {
        method:"POST",
        headers: {
          'Accept' : 'application/json',
          'Content-Type' : 'application/json',
        },
        body:JSON.stringify(this.state),
      });
    }catch(error){
      console.error(`Error is  : ` + error);
    }
window.location.reload();
  }

  render() {
    const { schools } = this.state;
    if(this.state.update){
      <div>
     <form onSubmit={this.handleClickEdit}>
          <FormGroup>
           <Segment inverted>
            <Table.Cell>
                <Table.Row> 
                        <Table.Cell>
                                <Input type="text" id="schoolN" value={this.state.schoolName} placeholder="School Name" name="sn" onChange={this.handleChangeSN}></Input>
                        </Table.Cell>
                        <Table.Cell>
                              <Input type="text" id="schoolA" value={this.state.schoolAddress} placeholder="School Address" name="sa" onChange={this.handleChangeSA}></Input>
                        </Table.Cell>
                        <Table.Cell>
                              <Input type="text" id="schoolP" value={this.state.schoolPhone} placeholder="School Phone" name="sp" onChange={this.handleChangeSP}></Input>
                        </Table.Cell>
                              <Button inverted type="submit" color="yellow" onSubmit={this.onSubmit} > Add </Button>
                              <Label as="a" basic color="blue"> SCHOOLS </Label>
                  </Table.Row>
            </Table.Cell>
          </Segment>
        </FormGroup>
      </form>
      </div>
    }else {
 return (
      <div>
        <form onSubmit={this.onSubmit}>
          <FormGroup>
           <Segment inverted>
            <Table.Cell>
                <Table.Row> 
                        <Table.Cell>
                                <Input type="text" id="schoolN" value={this.state.sn} placeholder="School Name" name="sn" onChange={this.handleChangeSN}></Input>
                        </Table.Cell>
                        <Table.Cell>
                              <Input type="text" id="schoolA" value={this.state.sa} placeholder="School Address" name="sa" onChange={this.handleChangeSA}></Input>
                        </Table.Cell>
                        <Table.Cell>
                              <Input type="text" id="schoolP" value={this.state.sp} placeholder="School Phone" name="sp" onChange={this.handleChangeSP}></Input>
                        </Table.Cell>
                              <Button inverted type="submit" color="yellow" onSubmit={this.onSubmit} > Add </Button>
                              <Label as="a" basic color="blue"> SCHOOLS </Label>
                  </Table.Row>
            </Table.Cell>
          </Segment>
        </FormGroup>
      </form>
        <Table ui selectable inverted table>
          <Table.Header>
            <Table.Row>
              <Table.HeaderCell>Name</Table.HeaderCell>
              <Table.HeaderCell>Address</Table.HeaderCell>
              <Table.HeaderCell>Phone</Table.HeaderCell>
              <Table.HeaderCell>Settings</Table.HeaderCell>
            </Table.Row>
          </Table.Header>
          {schools.map(school => (
            <Table.Row id={school.schoolId}>
              <Table.Cell>{school.schoolName}</Table.Cell>
              <Table.Cell>{school.schoolAddress}</Table.Cell>
              <Table.Cell>{school.schoolPhone}</Table.Cell>
              <Segment inverted>
                 <Table.Cell>
                    <Button type="button" inverted color="teal" onClick={() => {this.handleClickEdit(school.schoolId); }} > Edit </Button> 
                 </Table.Cell>
                <Table.Cell>
                  <Button type="button" inverted  color="red" onClick={() => { this.remove(school.schoolId); }} > Delete </Button>                    
                </Table.Cell>
              </Segment>
            </Table.Row>
          ))}
        </Table>
      </div>
    );
    }

  }
}

export default School;
